int dp[100001][20];

vector<int> e[100001];


void dfs(int x,int p){
	for(int it:e[x]){
		if(it!=p){
			dfs(it,x);
			for(int i=0;i<=19;i++)
				dp[x][i]+=dp[it][i];
		}
	}
	int who;
	for(who=19;who>=0;who--)
		if(dp[x][who]>1)
			break;
	who++;
	while(dp[x][who])	who++;
	dp[x][who]++;
	for(int i=0;i<who;i++)
		dp[x][i]=0;
}

