	}

	int ans = 0;
	while (true) {
		std::vector<int> checked(g.size(), -1);
		int p = 0;
		std::map<int, std::set<int>> map;
		REP(i, g.size()) {
			if (checked[i] >= 0) continue;
			if (g[i].size() == 1) {
				int c = g[i][0];
				if (checked[c] >= 0) continue;
				for(const auto& j : g[c]) {
					checked[j] = p;
				}
				checked[c] = p++;
			}
		}
		std::set<int> cset;
		REP(i, g.size()) {
			if (checked[i] >= 0) {
				if (cset.find(checked[i]) != cset.end()) continue;
				if (g[i].size() == 1) {
					int c = g[i][0];
					for (const auto& j : g[c]) {
						if (g[j].size() >= 2) {
							cset.insert(checked[c]);
