using namespace std;
int n,u,v,ans,dp[110000][33];
int edgenum,vet[210000],Next[210000],Head[110000];
void addedge(int u,int v){
	vet[++edgenum]=v;
	Next[edgenum]=Head[u];
	Head[u]=edgenum;
}
void dfs(int u,int fa){
	int v;
	for (int e=Head[u];e;e=Next[e]){
		v=vet[e];
		if (v!=fa){
			dfs(v,u);
			for (int i=0;i<=30;i++) dp[u][i]+=dp[v][i];
		}
	}
	v=0;
	for (int i=0;i<=30;i++)
		if (dp[u][i]>1) v=i+1;
	while (dp[u][v]) v++;
	ans=max(ans,v); dp[u][v]=1;
	for (int i=0;i<v;i++) dp[u][i]=0;
}
int main(){
	scanf("%d",&n);
	for (int i=1;i<n;i++){
		scanf("%d%d",&u,&v);
		addedge(u,v); addedge(v,u);
	}
	dfs(1,0);
	printf("%d\n",ans);
	return 0;
}

