ll gcd(ll a,ll b) { return b==0?a:gcd(b,a%b); }

const int MAXN=100000;
const int MAXM=MAXN-1;

int n;
int ghead[MAXN],gnxt[2*MAXM],gto[2*MAXM];

ll dfs(int at,int par) {
	ll ret=0;
	for(int x=ghead[at];x!=-1;x=gnxt[x]) {
		int to=gto[x]; if(to==par) continue;
		ll cur=dfs(to,at);
		if(cur>ret) ret=cur;
	}
	int me=0; while(ret&(1LL<<me)) ++me; ret|=1LL<<me; ret&=~((1LL<<me)-1);
	//printf("%d = %lld\n",at,ret);
	return ret;
}

void run() {
	scanf("%d",&n); REP(i,n) ghead[i]=-1;
	REP(i,n-1) {
		int a,b; scanf("%d%d",&a,&b); --a,--b;
		gnxt[2*i+0]=ghead[a]; ghead[a]=2*i+0; gto[2*i+0]=b;
		gnxt[2*i+1]=ghead[b]; ghead[b]=2*i+1; gto[2*i+1]=a;
	}
	ll res=dfs(0,-1);
	int ret=0; while(res>>(ret+1)) ++ret; printf("%d\n",ret);

}

int main() {
	run();
	return 0;
}

