int N;
VI G[100011];
VI X[100011];

int par[100011];
VI ord;
int U[100011];

void MAIN() {
    scanf("%d", &N);
    REP (i, N-1) {
	int x, y;
	scanf("%d%d", &x, &y);
	x--; y--;
	G[x].push_back(y);
	G[y].push_back(x);
    }

    ord.reserve(N);
    ord.push_back(0);
    REP (i_, N) {
	int v = ord[i_];
	EACH (e, G[v]) if (*e != par[v]) {
	    par[*e] = v;
	    ord.push_back(*e);
	}
    }

    VI buf; buf.reserve(N+2);

    REP (i_, N) {
	int v = ord[N-1-i_];
	int cnt = 0, ma = -1;
	EACH (e, G[v]) if (*e != par[v]) {
	    if (ma < X[*e].back()) {
		ma = X[*e].back();
		cnt = 0;
	    }
	    if (ma == X[*e].back()) {
		cnt++;
	    }
	}

	if (cnt == 0) {
	    U[v] = 0;
	    X[v].push_back(0);
	} else if (cnt >= 2) {
	    U[v] = ma+1;
	    X[v].push_back(ma+1);
	} else {
	    buf.resize(ma+2);
	    fill(buf.begin(), buf.end(), 0);
	    EACH (e, G[v]) if (*e != par[v]) {
		EACH (f, X[*e]) buf[*f] = 1;
	    }
	    REP (i, ma+2) {
		if (buf[i] == 0) {
		    U[v] = i;
		    buf[i] = 1;
		    break;
		} else {
		    buf[i] = 0;
		}
	    }
	    if (U[v] == ma+1) {
		X[v].push_back(ma+1);
	    } else {
		REP (i, ma+2) {
		    if (buf[i]) X[v].push_back(i);
		}
	    }
	}

    }

    int ma = *max_element(U, U+N);
    // rprintf("%d", U, U+N);
    printf("%d\n", ma);
}

int main() {
    int TC = 1;
