const int N=200005;
int n,x,y,tot,ne[N],fi[N],zz[N];
void jb(int x,int y){
	ne[++tot]=fi[x];
	fi[x]=tot;
	zz[tot]=y;
}
int dfs(int x,int y){
	int s1=0,s2=0,p=0;
	for (int k=fi[x];k;k=ne[k])
		if (zz[k]!=y){
			int p=dfs(zz[k],x);
			s2|=s1&p;
			s1|=p;
		}
	while (((1<<p)<s2)||(s1&(1<<p)))p++;
	return (s1>>p<<p)|(1<<p);
}
