int f[N][20],g[N][20];
void add(int x,int y){
	k++;a[k]=y;
	if (fi[x]==0)fi[x]=k;else ne[la[x]]=k;
	la[x]=k;
}
void dfs(int x,int fa){
	for (j=0;j<20;j++)f[x][j]=1;
	for (int i=fi[x];i;i=ne[i])if (a[i]!=fa){
		dfs(a[i],x);
		for (j=0;j<20;j++)f[x][j]&=f[a[i]][j],g[x][j]+=g[a[i]][j];
	}
	memset(b,0,sizeof b);
	for (int i=fi[x];i;i=ne[i])if (a[i]!=fa)b[w[a[i]]]++;
	for (j=19;j>=0;j--)if (b[j]>1){
		for (k=j;k<20;k++)if (f[x][k]){w[x]=k;break;}
		break;
	}
	if (!w[x]){for (j=0;j<20;j++)if (f[x][j]){w[x]=max(w[x],j);break;}}
	f[x][w[x]]=0;g[x][w[x]]++;
	for (j=0;j<w[x];j++)f[x][j]=1;
}
