		d[v]++;
		d[u]++;

		Map[{v, u}] = G[u].size();
		Map[{u, v}] = G[v].size();
		G[v].push_back({u, 1});
		G[u].push_back({v, 1});
	}

	while (true) {
		ans++;
		memset(Mark, 0, sizeof Mark);
		memset(down, 0, sizeof down);
		memset(up, 0, sizeof up);
		bool change = false;

		for (int i = 0; i < n; i++)
			if (!del[i] && !Mark[i]) {
				change = true;

				dfs1(i, i);
				dfs2(i, i, 0);
				findS(i, i);
				del[S] = true;
				//cout << "&   " << S << "\n";
				for (int j = 0; j < G[S].size(); j++)
					if (G[S][j].second) {
						int v = G[S][j].first;
						int ind = Map[{S, v}];

						d[v]--;
