{
  int N;
  scanf("%d", &N);

  FOR(i, 1, N + 1) alive[i] = 1;

  FOR(i, 1, N) {
    int a, b;
    scanf("%d%d", &a, &b);
    T[a].push_back(b);
    T[b].push_back(a);
  }

  printf("%d\n", solve(1));

  return 0;
}

