class FindCentored{
  public:
  tree t;
  ll res;
  ll dfs(ll x,ll from){
      ll sum=1,maxi=-1;
      for(auto y:t[x]){
	  if(y==from)continue;
	  ll val=dfs(y,x);
	  sum+=val,maxi=max(maxi,val);
      }
      ll rem=t.size()-sum;
      maxi=max(maxi,rem);
      if(maxi<=t.size()/2)res=x;
      return sum;
  }
  ll main(tree tt){t=tt;
      dfs(t.begin()->first,-1);
      return res;
  }
};FindCentored fc;

class DivideTree{
  public:
  tree t;
  tree cur;
  void make_edge(ll a,ll b){
      cur[a].push_back(b);
      cur[b].push_back(a);
  }
  void dfs(ll x,ll from,bool flag){
      if(flag)make_edge(x,from);
      for(auto y:t[x]){
	  if(y!=from)dfs(y,x,1);
      }
  }
  vector<tree> res;
  vector<tree> main(tree tt){t=tt;
      res.clear();
      //cout<<"reach_dt"<<endl;
      ll centor=fc.main(t);
      //cout<<"centor_end"<<endl;
      for(auto x:t[centor]){
	  cur.clear();
	  dfs(x,centor,0);
	  res.push_back(cur);
      }
      return res;
  }  
};DivideTree dt;

