template <class T> inline void setmax(T & a, T const & b) { a = max(a, b); }

int diameter(vector<vector<int> > const & tree) {
    pair<int, int> result = { -1, -1 };
    int dist = 0;
    function<void (int, int)> dfs = [&](int i, int parent) {
        setmax(result, make_pair(dist, i));
        ++ dist;
        for (int j : tree[i]) if (j != parent) {
            dfs(j, i);
        }
        -- dist;
    };
    dfs(0, 0);
    int k = result.second;
    result = { -1, -1 };
    dfs(k, 0);
    return result.first;
}

int main() {
    // input
    int n; scanf("%d", &n);
    vector<vector<int> > g(n);
    repeat (i, n - 1) {
        int a, b; scanf("%d%d", &a, &b); -- a; -- b;
        g[a].push_back(b);
        g[b].push_back(a);
    }
    // solve
    int d = diameter(g);
    int k = 0;
    for (int acc = 1; acc < d + 1; ) {
        acc = 2 * acc + 1;
        ++ k;
    }
    // output
    printf("%d\n", k);
    return 0;
}

