    scanf("%d", &N);
    REP (i, N-1) {
	int x, y;
	scanf("%d%d", &x, &y);
	x--; y--;
	G[x].push_back(y);
	G[y].push_back(x);
    }

    ord.reserve(N);
    ord.push_back(0);
    REP (i_, N) {
	int v = ord[i_];
	EACH (e, G[v]) if (*e != par[v]) {
	    par[*e] = v;
	    ord.push_back(*e);
	}
    }

    VI buf; buf.reserve(N+2);

    REP (i_, N) {
	int v = ord[N-1-i_];
	int ma = -1;
	EACH (e, G[v]) if (*e != par[v]) {
	    amax(ma, X[*e].back());
	}

	if (ma == -1) {
	    U[v] = 0;
	    X[v].push_back(0);
	} else {
	    buf.resize(ma+2);
	    fill(buf.begin(), buf.end(), 0);
	    EACH (e, G[v]) if (*e != par[v]) {
		EACH (f, X[*e]) buf[*f] = 1;
	    }
	    int at_least = 0;
	    REP (i, ma+2) if (buf[i] >= 2) at_least = i+1;
	    REP (i, ma+2) {
		if (i < at_least || buf[i]) {
		    buf[i] = 0;
		} else {
		    U[v] = i;
		    buf[i] = 1;
		    break;
		}
	    }
	    REP (i, ma+2) if (buf[i]) X[v].push_back(i);
	}
    }

    int ma = *max_element(U, U+N);
    //rprintf("%d", U, U+N);
    printf("%d\n", ma);
}

