void work(int u){
	int gg=0,tmp=0;
	for (int i=g[u];~i;i=e[i].next)if(e[i].v!=pre[u]){
		gg|=f[e[i].v]&tmp;
		tmp|=f[e[i].v];
	}
	if(gg>0){
		while(gg&(gg-1))gg&=gg-1;
		tmp|=gg|(gg-1);
	}
	f[u]=tmp+1;
}
