using namespace std;

//#define FILE_IO

int N;
vector <int> edg[100005];
int h[100005], sz[100005];
int cntr[100005];

void DFS(int nod, int fth)
{
    h[nod] = 1 + h[fth];
    sz[nod] = 1;
    for(auto nxt: edg[nod])
        if(nxt != fth && !cntr[nxt])
        {
            DFS(nxt, nod);
            sz[nod] += sz[nxt];
        }
}

int getCentroid(int nod, int szmax)
{
    for(auto nxt: edg[nod])
        if(sz[nod] > sz[nxt] && sz[nxt] > szmax)
            return getCentroid(nxt, szmax);
    return nod;
}

int solve(int nod)
{
    DFS(nod, 0);
    if(sz[nod] == 1)    return 0;
    int root = getCentroid(nod, sz[nod] / 2);
    cntr[root] = 1;

    int ans = 0;
    for(auto nxt: edg[root])
        if(!cntr[nxt])
        {
            int hh = solve(nxt);
            ans = max(ans, hh);
        }
    ans++;
    return ans;
}

int main()
{
    #ifdef FILE_IO
    freopen("1.in", "r", stdin);
    freopen("1.out", "w", stdout);
    #endif

    scanf("%d", &N);
    for(int i = 1; i < N; i++)
    {
        int x, y;
        scanf("%d%d", &x, &y);
        edg[x].push_back(y);
        edg[y].push_back(x);
    }

    int ans = solve(1);
    printf("%d\n", ans);

    return 0;
}

