const int MAXN = 1e5 + 10;

int N, totv, ans, vis[MAXN],V[MAXN], sz[MAXN], mxsz[MAXN];
std::vector<int> G[MAXN];

void dfs(int, int);

int main() {
	scanf("%d", &N);
	for (int i = 1, ui, vi; i < N; i++) {
		scanf("%d%d", &ui, &vi);
		G[ui].push_back(vi);
		G[vi].push_back(ui);
	}

	dfs(1, 0);

	printf("%d\n", ans);
	return 0;
}

void dfs1(int u, int fa) {
	V[totv++] = u, sz[u] = 1, mxsz[u] = 0;
	for (auto v : G[u]) {
		if (vis[v] || v == fa) continue;
		dfs1(v, u);
		sz[u] += sz[v], mxsz[u] = std::max(mxsz[u], sz[v]);
	}
}

int find_cntr(int u) {
	totv = 0;
	dfs1(u, -1);
	for (int i = 0; i < totv; i++) {
		int v = V[i];
		mxsz[v] = std::max(mxsz[v], sz[u] - sz[v]);
		if (mxsz[v] * 2 <= sz[u]) return v;
	}
}

void dfs(int u, int dep) {
	ans = std::max(dep, ans);
	u = find_cntr(u);
	vis[u] = 1;

	for (auto v : G[u]) {
		if (vis[v]) continue;
		dfs(v, dep + 1);
	}
}

