int n,tote,to[N<<1],nxt[N<<1],con[N],w[N],ans;
void ins(int x,int y)
{
	to[++tote]=y;
	nxt[tote]=con[x];
	con[x]=tote;
}
void dfs(int v,int fa)
{
	int dep=-1;
	bool flag=0;
	for(int p=con[v];p;p=nxt[p])
		if(to[p]!=fa)
		{
			dfs(to[p],v);
			int tmp=w[v]&w[to[p]];
			for(int i=30;i>=0;i--)
				if((tmp>>i)&1) {dep=max(dep,i);break;}
			w[v]=w[v]|w[to[p]];
			flag=1;
		}	
	if(!flag) {w[v]=1;return ;}	
	for(int i=dep+1;i<=30;i++)
		if((w[v]>>i)&1) 
			dep=max(dep,i);
		else break;
	ans=max(ans,dep+1);
	int R=(1<<(dep+1)),T=((1<<30)-1)^(R-1);
	w[v]=(w[v]&T)|R;	
}
int main()
{
	scanf("%d",&n);
	for(int i=1;i<n;i++)
	{
		int x,y;
		scanf("%d%d",&x,&y);
		ins(x,y);ins(y,x);
	}
	dfs(1,0);
	printf("%d",ans);
	return 0;
}

