using namespace std;
template <class T> inline void setmax(T & a, T const & b) { a = max(a, b); }

int get_size(int root, vector<vector<int> > const & tree, vector<bool> const & used) {
    function<int (int, int)> dfs = [&](int i, int parent) {
        int size = 1;
        for (int j : tree[i]) if (j != parent and not used[j]) {
            size += dfs(j, i);
        }
        return size;
    };
    return dfs(root, -1);
}
int get_centroid(int root, vector<vector<int> > const & tree, vector<bool> const & used) {
    int n = get_size(root, tree, used);
    int centroid = -1;
    int centroid_weight = INT_MAX;
    function<int (int, int)> dfs = [&](int i, int parent) {
        int acc_weight = 0;
        int max_weight = -1;
        for (int j : tree[i]) if (j != parent and not used[j]) {
            int weight = dfs(j, i);
            acc_weight += weight;
            setmax(max_weight, weight);
        }
        setmax(max_weight, n - (1 + acc_weight));
        if (max_weight < centroid_weight) {
            centroid_weight = max_weight;
            centroid = i;
        }
        return 1 + acc_weight;
    };
    dfs(root, -1);
    return centroid;
}

int main() {
    // input
    int n; scanf("%d", &n);
    vector<vector<int> > g(n);
    repeat (i, n - 1) {
        int a, b; scanf("%d%d", &a, &b); -- a; -- b;
        g[a].push_back(b);
        g[b].push_back(a);
    }
    // solve
    vector<bool> used(n);
    function<int (int)> go = [&](int i) {
        i = get_centroid(i, g, used);
        used[i] = true;
        int k = 0;
        for (int j : g[i]) if (not used[j]) {
            setmax(k, go(j) + 1);
        }
        return k;
    };
    int k = go(0);
    // output
    printf("%d\n", k);
    return 0;
}

