using namespace std;

vector<vector<int>> graph;
vector<int> label;

int dfs_init(int node, int par) {
    int mask = 0, beat = 0;
    for (auto vec : graph[node]) {
        if (vec == par) continue;
        int now = dfs_init(vec, node);
        beat |= (mask & now);
        mask |= now;
    }
    int need = 31 - __builtin_clz(beat);
    mask |= (1 << need) - 1;
    label[node] = __builtin_ctz(~mask);
    mask ^= (2 << label[node]) - 1;
    return mask;
}

