using Graph = vector<vector<int>>;
using Level = int;
using Upper = bool;
using Status = pair<Level, Upper>;
using Index = int;

int debugcnt = 0;
Status get_status(const vector<Status>& statuses) {
    debugcnt += statuses.size();
    assert(debugcnt < 1e7);
    Status res;
    for (Status status : statuses)res = max(res, status);
    return res;
}

set<int> duplicate;
Index get_parent(const Graph& graph, const set<Index>& liftupped, const Index now) {
    assert(duplicate.find(now) == duplicate.end());
    duplicate.insert(now);
    for (Index next : graph[now])
        if (liftupped.find(next) == liftupped.end())
            return next;
    return -1;
}

Status next_status(const Status& status) {
    if (status.second) {
        return { status.first + 1,false };
    }
    else {
        return { status.first,true };
    }
}

int max_level(const map<Level, int>& cnt) {
    int base = -1;
    for (const auto& itr : cnt)base = max(base, itr.first);
    return base;
}

