        int a, b;
        cin >> a >> b;
        a--; b--;
        graph[a].push_back(b);
        graph[b].push_back(a);
    }

    vector<vector<Status>> raw_statuses(N);
    queue<Index> liftups;
    set<Index> liftupped;
    rep(i, N)if (graph[i].size() == 1) {
        raw_statuses[i].push_back({ 0,true });
        liftups.push(i);
        liftupped.insert(i);
    }
    while (!liftups.empty()) {
        Index now = liftups.front(); liftups.pop();
        Index parent = get_parent(graph, liftupped, now);
        if (parent == -1)break;
        Status status = get_status(raw_statuses[now]);
        Status parent_status = next_status(status);
        raw_statuses[parent].push_back(parent_status);

        if (raw_statuses[parent].size() + 1 == graph[parent].size()) {
            assert(liftupped.find(parent) == liftupped.end());
            liftups.push(parent);
            liftupped.insert(parent);
        }
    }
    map<Level, int> cnt;
    for (auto& raw_status : raw_statuses) {
        auto status = get_status(raw_status);
        cnt[status.first]++;
    }
    int level = max_level(cnt);
    if (cnt[level] > 1)level++;
