	to[++E] = v, next[E] = first[u], first[u] = E;
	to[++E] = u, next[E] = first[v], first[v] = E;
}

void dfs(int x, int px = 0) {
	int i, y, s = 0;
	for (i = first[x]; i; i = next[i])
		if ((y = to[i]) != px)
			dfs(y, x), s |= f[x] & f[y], f[x] |= f[y];
	s = lg2(s << 1 | 1), s = ctz(~(f[x] >> s));
	f[x] = (f[x] >> s | 1) << s, ans[x] = s;
}

