  for (int i = 0; i < E[x].size(); i++) {
    int nxt = E[x][i];
    if (bio[nxt] || nxt == p) 
      continue;
    dfs(nxt, x);
    sz[x] += sz[nxt];
    maks[x] = max(maks[x], sz[nxt]);
  }
}

void centroid(int x, int p, int d) {
  V.clear();
  dfs(x, p);
  int best = -1;
  for (int i = 0; i < V.size(); i++) {
    maks[V[i]] = max(maks[V[i]], sz[x] - sz[V[i]]);
    if (best == -1 || maks[V[i]] < maks[best]) 
      best = V[i];
  }
  x = best;
  sol = max(sol, d);
  bio[x] = true;
  for (int i = 0; i < E[x].size(); i++) {
    int nxt = E[x][i];
    if (bio[nxt]) 
      continue;
    centroid(nxt, x, d + 1);
  }
}

