using namespace std;

class Tree {
  public:
    Tree(int size):
            m_edges(size),
            m_size(size) {}

    int size() const {
        return m_edges.size();
    }

    void addEdge(int x, int y) {
        m_edges[x].emplace_back(y);
        m_edges[y].emplace_back(x);
    }

    int uninity() {
        return dfsUninity(0);
    }

  private:
    int dfsUninity(int node) {
        if (m_edges[node].size() == 0)
            return 0;

        dfsSize(node);
        int center = node;
        while (true) {
            bool found_next = false;
            for (auto &x : m_edges[center])
                if (m_size[x] < m_size[center] && m_size[x] > m_size[node] / 2) {
                    center = x;
                    found_next = true;
                    break;
                }
            if (!found_next)
                break;
        }

        int answer = 0;
        for (auto &x : m_edges[center]) {
            m_edges[x].erase(find(m_edges[x].begin(), m_edges[x].end(), center));
            answer = max(answer, dfsUninity(x));
        }
        return answer + 1;
    }

    void dfsSize(int node, int father = -1) {
        m_size[node] = 1;
        for (auto &x : m_edges[node])
            if (x != father) {
                dfsSize(x, node);
                m_size[node] += m_size[x];
            }
    }

    vector< vector<int> > m_edges;
    vector<int> m_size;
};

int main() {
    int N; cin >> N;

    Tree T(N);
    for (int i = 1; i < N; ++i) {
        int x, y; cin >> x >> y;
        T.addEdge(x - 1, y - 1);
    }

    cout << T.uninity() << "\n";
}

