const int N = (int)1e5+5;
int n, n2, sz[N], ans[N];
bool vis[N];
vector< vector<int> > g;

void dfs_sz(int u, int p) {
	sz[u] = 1, ++n2;
	REP(i, 0, SZ(g[u])) {
		int v = g[u][i];
		if (vis[v] || v == p) continue;
		dfs_sz(v, u);
		sz[u] += sz[v];
	}
}

int dfs_center(int u, int p) {
	REP(i, 0, SZ(g[u])) {
		int v = g[u][i];
		if (vis[v] || v == p) continue;
		if (sz[v] > n2 / 2) return dfs_center(v, u);
	}
	return u;
}

void decompose(int u, int depth) {
	n2 = 0;
	dfs_sz(u, 0);
	int mid = dfs_center(u, 0);
	ans[mid] = depth, vis[mid] = true;
	REP(i, 0, SZ(g[mid])) {
		int v = g[mid][i];
		if (vis[v]) continue;
		decompose(v, depth + 1);
	}
}

