const int N = 123456;

int n, foo[N], key[N], answer;
vector<int> adj[N];

void dfs(int x, int f) {
  int bar = 0;
  for (auto y : adj[x]) {
    if (y != f) {
      dfs(y, x);
      bar |= foo[x] & foo[y];
      foo[x] |= foo[y];
    }
  }
  if (!foo[x]) {
    key[x] = 0;
    foo[x] = 1;
  } else {
    while ((1 << key[x]) <= bar || (foo[x] >> key[x] & 1)) {
      ++key[x];
    }
    for (int i = 0; i < key[x]; ++i) {
      foo[x] -= (foo[x] >> i & 1) << i;
    }
    foo[x] |= 1 << key[x];
    answer = max(answer, key[x]);
  }
}

