int ans = 0;

int dp(int u, int p){
	int cnt[20];
	fill(cnt,cnt+20,0);
	for(int v : adj[u]){
		if(v == p) continue;
		int mask = dp(v, u);
		for(int i = 0;i < 20;i++){
			if(((1 << i) & mask)) cnt[i]++;
		}
	}

	int bit = 19;
	for(;bit >= 0 && cnt[bit] <= 1;bit--) continue;
	for(bit++;cnt[bit] > 0;bit++) continue;
	ans = max(ans,bit);
	int mask = 1 << bit;
	for(int i = bit;i < 19;i++){
		if(cnt[i] > 0) mask |= (1 << i);
	}

	return mask;
}

