int const N = 3e5 + 41;
int const M = 25;

int n;
int d[N];
vector<int> e[N];

int dfs(int u, int p){
	int atleastOne = 0;
	int atleastTwo = 0;
	rep(i, 0, sz(e[u])){
		int to = e[u][i];
		if(to == p) continue;
		int res = dfs(to, u);
		atleastTwo|= (atleastOne & res);
		atleastOne |= res;
	}
	int ret;
	rep(i, 0, M){
		if( ((1<<i) > atleastTwo) && !(atleastOne&(1<<i)) ){
			ret = i;
			break;
		}
	}

	int result = (1 << ret);
	rep(i, ret, M){
		result |= ((1 << i) & atleastOne);
	}

	return d[u] = result;
}

