    inline void addEdge(int tmpu, int tmpv){
        v = tmpv;
        next = front[tmpu];
        front[tmpu] = tot;
    }
} e[N<<1];
inline void add(int u, int v){
    e[++tot].addEdge(u, v);
    e[++tot].addEdge(v, u);
}

void dfs(int u, int fa){
    bit[u] = f[u] = 0; int l = 0;
    for(int i=front[u]; i; i=e[i].next){
        int v = e[i].v;
        if(v == fa) continue;

		dfs(v, u);
        l |= (bit[u] & bit[v]);
        // bit[u]  bit[v]  i 1, l  i 1
        //  i ,  i
        bit[u] |= bit[v];
    }

    if(!bit[u]) bit[u] = 1;
    else{
        while((1<<f[u]) < l || (1<<f[u]) & bit[u]) f[u] ++;
        //2^f[u] < l  bit[u]f[u]1
        bit[u] = ((bit[u] >> f[u] << f[u]) | (1<<f[u]));
        //bit[u], f[u]
    }

    ans = max(ans, f[u]);
}

