void addedge(int b, int e) {
	nxt[++tot] = head[b]; to[head[b] = tot] = e;
	nxt[++tot] = head[e]; to[head[e] = tot] = b;
}
int n;
int dfs(int u, int fa = 0) {
	int pre = 0, hav = 0;
	for (int i = head[u]; i; i = nxt[i]) if (to[i] != fa) {
		int t = dfs(to[i], u);
		hav |= pre & t;
		pre |= t;
	}
	int p = hav ? std::__lg(hav) + 1 : 0;
	p = __builtin_ctz((pre >> p) + 1);
	pre ^= pre & (1 << p) - 1;
	return pre | 1 << p;
}
