inline int Log(int x) {
  int res = 0;
  if (x >= (1 << 16)) x >>= 16, res += 16;
  if (x >= (1 << 8)) x >>= 8, res += 8;
  if (x >= (1 << 4)) x >>= 4, res += 4;
  if (x >= (1 << 2)) x >>= 2, res += 2;
  if (x >= (1 << 1)) x >>= 1, res += 1;
  return res;
}

inline void Dfs(int x, int f) {
  int msk = 0, t = 0;
  travel(x, y, i) {
    if (y == f) continue;
    Dfs(y, x);
    int tmp = dp[y];
    for (int j = 17; j > t; j --)
      if ((msk & tmp) & (1 << j)) t = j;
    msk |= tmp;
  }
  while (msk & (1 << t)) t ++;
  msk |= (1 << t) - 1;
  msk ^= (1 << t) - 1;
  msk |= (1 << t);
  dp[x] = msk;
}

