int solve(int pos, int pre, vector<vector<int> > &g) {
	int req = 0, mx = 0;
	vector<int> lens;
	for (int i : g[pos]) {
		if (i == pre) continue;
		int res = solve(i, pos, g);
		int res_req = 0;
		int res_len = 0;
		while (2 << res_len < res) ++res_len;
		while ((res >> res_req) & 1) ++res_req;
		req = max(req, res_req);
		mx = max(mx, res);
		lens.push_back(res_len);
	}
	sort(lens.begin(), lens.end(), greater<int>());
	if (lens.size() >= 2) req = max(req, lens[1] + 1);
	++mx;
	while (req != 0 && (mx & (1 << (req - 1)))) ++mx;
	return mx;
}
