static vector<int> e[2][100000];

int recon(int i,int p,bool b,int &N)
{
  //printf("%d %d %d\n",i,p,N);
  int t=N;
  e[!b][t]=vector<int>(0);
  N++;
  int S=0;
  for(int x=0;x<e[b][i].size();x++){
    if(e[b][i][x].size()==1){
      S++;
    }
  }
  for(int x=0;x<e[b][i].size();x++){
    int j=e[b][i][x];
    if(j==p)continue;
    if(e[b][j].size()==2&&S>=e[b][i].size()-1){
      int k=(e[b][j][0]==i?e[b][j][1]:e[b][j][0]);
      int c=recon(k,j,b,N);
      e[!b][t].push_back(c);
      e[!b][c].push_back(t);
    }
    else if(e[b][j].size()>=2){
      int c=recon(j,i,b,N);
      e[!b][t].push_back(c);
      e[!b][c].push_back(t);
    }
  }
  return t;
}

