struct Node{
    vector<Node*> dests;
    void add(Node* n){
        dests.push_back(n);
    }
    void add2(Node* n){
        add(n);
        n->add(this);
    }
    pair<int, Node*> dfs(Node* from = nullptr){
        pair<int, Node*> ret = {0, this};
        for(Node* d : dests){
            if(d == from){ continue; }
            auto p = d -> dfs(this);
            p.first++;
            ret = max(ret, p);
        }
        return ret;
    }
};

int main(){
    int n; cin >> n;
    vector<Node> nodes(n);
    for(int i=0;i<n;i++){
