    printd(d);
    int num = 0, ans = 0;
    for(int i=1;i<M;i++){
        num = num * 2 + 2;
        prints(num, d, i);
        if(num >= d){ ans = i; break; }
    }
    cout << ans << '\n';
    return 0;
}

