const int N=50000+5;  
struct Edge{int to,next;}e[N<<1];  
int head[N],cnt;  
void ins(int u,int v){  
    e[++cnt]=(Edge){v,head[u]};head[u]=cnt;  
}  
int f[N],bin[30],s[N],m,c[30];  
void dp(int u,int fa){  
     int sv=0,cu=0,son=0;  
     for(int i=head[u];i;i=e[i].next){  
        int v=e[i].to;if(v==fa)continue;  
        dp(v,u);  
        sv|=s[v];  
        son++;  
     }  
     if(!son){  
        f[u]=0;  
        s[u]=1;  
        return;  
     }  
     memset(c,0,sizeof(c));  
     for(int i=head[u];i;i=e[i].next)  
     if(e[i].to!=fa)  
     for(int j=0;j<m;j++)  
     if(s[e[i].to]&(1<<j))c[j]++;  
     if(son>1)  
     for(int i=m-1;i>=0;i--)  
     if(c[i]>=2){  
        cu=i+1;  
        break;  
     }  
     for(int i=cu;i<m;i++)  
     if(!(sv&(1<<i))){  
        cu=i;  
        break;  
     }  
     s[u]=((sv>>cu)|1)<<cu;  
     f[u]=cu;  
     for(int i=head[u];i;i=e[i].next)  
     if(e[i].to!=fa)  
     f[u]=max(f[u],f[e[i].to]);  
}  
