void addedge(int u,int v)
{
	e[cnt]=(edge){v,h[u]},h[u]=cnt++;
	e[cnt]=(edge){u,h[v]},h[v]=cnt++;
}

int high(int s)
{
	if(!s) return -1;
	if(!(s&65535)) return 16+hg[s>>16];
	return hg[s&65535];
}

int mex(int s)
{
	if((s&65535)==65535) return 16+mx[s>>16];
	return mx[s&65535];
}

void dfs(int x,int fa)
{
	int s0=0,s1=0;
	for(int i=h[x]; i!=-1; i=e[i].n)
		if(e[i].v!=fa)
		{
			dfs(e[i].v,x);
			s0|=S[e[i].v]&s1,s1|=S[e[i].v];
		}
	int p=high(s0);
	id[x]=mex(s1>>p+1)+p+1;
	S[x]=(s1>>id[x]<<id[x])|(1<<id[x]);
}

