	vector<vector<UL>> E;

	UL ans = 0;
	unsigned Solve2(UL p, UL pre) {
		UL F = 0;
		for (auto next : E[p]) {
			if (next == pre) continue;
			UL d = Solve2(next, p);
			ans = max(ans, F + d + 1);
			F = max(F, d);
		}
		return F + 1;
	}

