int N;
map<int, vector<int>> to;
vector<int> st;
set<int> leaf;
int n_visited;

void solve(int uni)
{
    if (uni > 0) solve(uni - 1);
    set<int> new_leaf;
    for (auto v : leaf) if (st[v] < 0)
    {
        st[v] = uni;
        n_visited++;
        for (auto nx : to[v]) if (st[nx] < 0) new_leaf.insert(nx);
    }
    leaf = new_leaf;
    if (uni > 0) solve(uni - 1);
}

int main()
{
    cin >> N;
    REP(_, N - 1)
    {
        int a, b;
        cin >> a >> b;
        to[a - 1].push_back(b - 1);
        to[b - 1].push_back(a - 1);
    }
    set<int> leaf_init;
    REP(i, N) if (to[i].size() == 1) leaf_init.insert(i);

    int l = -1, r = 18;
    while (r - l > 1)
    {
        int c = (l + r) / 2;
        st.assign(N, -1);
        n_visited = 0;
        leaf = leaf_init;
        solve(c);
        int cou = 0;
        for (auto v : st) cou += (v == c);
        ((n_visited == N and cou <= 1) ? r : l) = c;
    }
    cout << r << endl;
}

