void bfs01(Graph &g, vector<int> &d, int s) {
  d.assign(g.size(), INF);
  d[s] = 0;
  typedef pair<Weight,int> P;
  deque<P> que;
  que.push_back(P(0, s));
  while (!que.empty()) {
    P top = que.front(); que.pop_front();
    Weight dist = top.first; int v = top.second;
    if (d[v] < dist) continue;
    REP(i, g[v].size()) {
      Edge e = g[v][i];
      if (d[e.dest] > d[v] + e.weight) {
        d[e.dest] = d[v] + e.weight;
        if (e.weight) que.push_back(P(d[e.dest], e.dest));
        else que.push_front(P(d[e.dest], e.dest));
      }
    }
  }
}

int logg(ll n) {
  if (n == 1) return 0;
  return logg(n / 2) + 1;
}

bool visited[128000];
ll dfs(const Graph &g, int v) {
  visited[v] = true;
  vector<ll> vec;
  for (auto i: g[v]) {
    if (visited[i.dest]) continue;
    vec.push_back(dfs(g, i.dest));
  }
  sort(ALL(vec));
  if (vec.empty()) return 1;
  if (vec.size() == 1) {
    return vec.back() + 1;
  }
  ll res = vec.back() + 1;
  // cout << log(res)
  if (logg(res) == logg(res + vec[vec.size() - 2])) {
    if (res % 2 == 1) return res + 1;
    return res;
  }
  return ll(1) << (logg(res) + 1);
}

void solve(ll N, vector<ll> a, vector<ll> b) {
  Graph g(N);
  REP(i,N-1) {
    add_edge(g, a[i] - 1, b[i] - 1, 1);
    add_edge(g, b[i] - 1, a[i] - 1, 1);
  }
  ll res = dfs(g, 0);
  // cout << res << endl;
  cout << logg(res) << endl;
}

