pair<int,int> dfs(int par ,int cur,Graph &t){
    if((int)t[cur].size()==1 and t[cur][0].to==par){
        return{0,1};
    }
    int sum=0,prd=0,f=0;
    for(auto &e: t[cur]){
        if(e.to!= par){
            auto sub = dfs(cur,e.to,t);
            f=max(f,sub.first);
            prd |= sum&sub.second;
            sum |= sub.second;
        }
    }
    int res=0;
    while(prd&(1<<res))res++;
    while(sum&(1<<res))res++;
    return {max(f,res),(((sum>>res)^1)<<res)};
}

