const int MAXN = 100005;

vector<int> G[MAXN];

int dp[3][MAXN];
int dep[MAXN];

int A[MAXN], B[MAXN];

int N, Ans = INF;

void f(int i) {
	int a[3] = {-1, -1, -1};
	for(int v : G[i]) if(!dep[v]) {
		dep[v] = dep[i] + 1;
		f(v);
		upmax(dp[1][i], dp[0][v]);
		upmax(dp[2][i], dp[1][v]);

		a[2] = dp[0][v];
		sort(a, a+3);
		reverse(a, a+3);
	}
	if(a[0] < 0) return;

	int &ret = dp[0][i];
	ret = dp[2][i] + 1;
	//printf("i=%d, ret=%d\n", i, ret);

	for(int v : G[i]) if(dep[i] < dep[v]) {
		int x = (a[0] == dp[0][v] ? a[1] : a[0]);
		upmin(ret, max(x, dp[2][v]) + 1);
		//printf("i=%d, v=%d :: x=%d, dp=%d\n", i, v, x, dp[2][v]);
	}
}

void g(int i, int x) {
	int a[3] = {-1, -1, -1};
	for(int v : G[i]) if(dep[i] < dep[v]) {
		a[2] = dp[0][v];
		sort(a, a+3);
		reverse(a, a+3);
	}
	if(a[0] < 0) {
		upmin(Ans, max(0, x));
		return;
	}
	for(int v : G[i]) if(dep[i] < dep[v]) {
		int t = (a[0] == dp[0][v] ? a[1] : a[0]);
		g(v, max(t, x) + 1);
		upmin(Ans, dp[0][v] + max(t, x) + 1);
	}
}

