vi vs;
bool vfs(int v, int t, int p=-1) {
  if (v == t) {
    vs.pb(v);
    return true;
  }
  for (int u : to[v]) {
    if (c[u] || p == u) continue;
    if (vfs(u,t,v)) {
      vs.pb(v);
      return true;
    }
  }
  return false;
}
int dfs(int v) {
