struct edges
{
	int to,next;
}edge[202020];
int first[101010],now;
void adde(int x,int y)
{
	now++;
	edge[now].to=y;
	edge[now].next=first[x];
	first[x]=now;
}
int s[101010],al=(1<<20)-1;
int pre[1<<20];
int lowbit(int x)
{
	return x&(-x);
}
int h=0;
void work(int p,int fa)
{
	int mk=1;
	s[p]=0;
	for(int e=first[p];e;e=edge[e].next)
		if(edge[e].to!=fa)
		{
			work(edge[e].to,p);
			mk=max(mk,pre[s[p]&s[edge[e].to]]);
			s[p]|=s[edge[e].to];
		}
	while(mk&s[p])
		mk<<=1;
	h=max(h,mk);
	s[p]&=(al^(mk-1));
	s[p]^=mk;
}
int main()
{
