		pre[i]=pre[i>>1]<<1;
	for(int i=1;i<n;i++)
	{
		scanf("%d%d",&x,&y);
		adde(x,y);
		adde(y,x);
	}
	work(1,0);
	ans=-1;
	while(h)
	{
		ans++;
		h>>=1;
	}
	printf("%d\n",ans);
	return 0;
}

