#include <bits/stdc++.h>

using namespace std;

vector<vector<int>> graph;
vector<int> label;

int dfs_init(int node, int par) {
    int mask = 0, beat = 0;
    for (auto vec : graph[node]) {
        if (vec == par) continue;
        int now = dfs_init(vec, node);
        beat |= (mask & now);
        mask |= now;
    }
    int need = 32 - __builtin_ctz(beat);
    mask |= (1 << need) - 1;
    label[node] = __builtin_ctz(~mask);
    mask ^= (2 << label[node]) - 1;
    return mask;
}

int main() {
    int n; cin >> n;
    label.resize(n);
    graph.resize(n);
    for (int i = 1; i < n; ++i) {
        int a, b; cin >> a >> b;
        graph[a - 1].push_back(b - 1);
        graph[b - 1].push_back(a - 1);
    }
    dfs_init(0, -1);
    int res = 0;
    for (int i = 0; i < n; ++i) {
        res = max(res, label[i]);
    }
    cout << res + 1 << endl;

    return 0;
}

