vector<P>G[200000];
int ret=-1;
bool rem[200005]={};
int sub[200005]={},len;
int ind;
P xz[200005];
int make(int v,int u)
{
	int cnt=1;
	for(int i=0;i<G[v].size();i++)
	{
		int v2=G[v][i].f;
		if(rem[v2] || v2==u) continue;
		cnt+=make(v2,v);
	}
	return sub[v]=cnt;
}
P search(int v,int u,int siz)
{
	//size and index
	P res=mp(INF,-1);
	int s=1,m=0;
	for(int i=0;i<G[v].size();i++)
	{
		int v2=G[v][i].f;
		if(rem[v2] || v2==u) continue;
		res=min(res,search(v2,v,siz));
		m=max(m,sub[v2]);
		s+=sub[v2];
	}
	m=max(m,siz-s);
	res=min(res,mp(m,v));
	return res;
}
void dfs(int v,int u,int dist,int d)
{
	if(ret<d || dist>len) return ;
	xz[ind++]=mp(dist,d);
	for(int i=0;i<G[v].size();i++)
	{
		int v2=G[v][i].f;
		if(rem[v2] || v2==u) continue;
		dfs(v2,v,dist+G[v][i].s,d+1);
	}
}
void sol(int v,int d)
{
	make(v,-1);
	int cent=search(v,-1,sub[v]).s;
	rem[cent]=true;
	ret = max(ret,d);
	for(int i=0;i<G[cent].size();i++) if(!rem[G[cent][i].f]) sol(G[cent][i].f,d+1);
	rem[cent]=false;
}
int best_path(int n,int k,int e[][2],int leng[])
{
	len=k;
	for(int i=0;i<n-1;i++)
	{
		int u=e[i][0];
		int v=e[i][1];
		G[u].pb(mp(v,leng[i]));
		G[v].pb(mp(u,leng[i]));
	}
	sol(1,0);
	return ret;
}
