struct Edge
{
    int to, nxt;
};
Edge e[100010];
int head[100010], cnt;
const int LG = 60;
int dp[100010][LG + 1];

void add_edge(int a, int b)
{
    e[++cnt].to = b;
    e[cnt].nxt = head[a];
    head[a] = cnt;
}

void dfs(int nod, int tata)
{
    for (int i = head[nod]; i; i = e[i].nxt) {
        int v = e[i].to;
        if (v == tata)
            continue;
        dfs(v, nod);
        for (int j(1); j <= LG; j++)
            dp[nod][j] += dp[v][j];
    }
    int bigg0(1);
    for (int i(1); i <= LG; i++) {
        if (dp[nod][i] <= 1)
            continue;
        int p(i);
        while (dp[nod][p])
            p++;
        dp[nod][p] = 1;
        for (int i(bigg0); i < p; i++)
            dp[nod][i] = 0;
        bigg0 = p;
    }

    int p(1);
    while (dp[nod][p])
        p++;
    dp[nod][p] = 1;
    for (int i(1); i < p; i++)
        dp[nod][i] = 0;
}

