vector<int> adj[100009];

int dfs(int x,int p){//returns bitmask of visible positions
	bool hasc=0;
	int visible=0;
	int doubleup=0;
	for(int i:adj[x]){
		if(i==p)continue;
		hasc=1;
		int dfsv=dfs(i,x);
		doubleup|=(visible&dfsv);
		visible|=dfsv;
	}
	if(!hasc)return 1;
	int m1=0,m2=0;
	REP(i,20){
		if(!(visible&(1<<i))){m1=i;break;}
	}
	for(int i=19;i>=0;i--){
		if(doubleup&(1<<i)){m2=i;break;}
	}
	int v=max(m1,m2);
	visible|=(1<<v);
	REP(i,v){
		if(visible&(1<<i))
		visible^=(1<<i);
	}
	return visible;
}

