set solve(ll x,ll from){
  ll cnt[D]; for(int i=0;i<D;i++)cnt[i]=0;
  for(auto y:g[x])if(y!=from){
    set cur=solve(y,x);
    for(auto z:cur)cnt[z]++;
  }
  ll clash=-1;
  for(ll i=0;i<D;i++){
    if(cnt[i]>=2){clash=i; break;}
  }
  ll gen;
  for(ll i=clash+1;;i++){
    if(cnt[i]==0){gen=i; break;}
  }
  set res; res.insert(gen);
  for(ll i=gen+1;i<D;i++){
    if(cnt[i]>0)res.insert(i);
  }
  return res;
}
