void trace(int v)
{
	if (used[v]==T)
		return;
	used[v]=1;
	comp.push_back(v);
	for (int i=0;i<g[v].size();i++)
	{
		int id=g[v][i];
		if (dead[id])
			continue;
		int to=get_other(id,v);
		if (used[to])
			continue;
		trace(to);
	}
}

